def string_composition(k, dna):
    kmers = list()
    f = open("String_Composition.txt", "w")
    for i in range(len(dna)-k+1):
        kmer = dna[i:i+k]
        kmers.append(kmer)
        f.write("%s\n" %kmer)
    f.close()

if __name__ == '__main__':
    k = 100
    dna = ""
    string_composition(k,dna)   